(()=>{document.getElementById("complaintForm").addEventListener("submit",async s=>{s.preventDefault();let o=document.querySelector(".confirmed-message"),e=document.getElementById("submitComplaint"),n=e.innerHTML;e.innerHTML="Please, wait...",e.classList.add("disabled"),e.disabled=!0;let t=s.target;(await fetch(t.action,{method:"POST",body:new FormData(t)})).ok&&(t.reset(),o.style.display="block",window.scrollTo({top:0,left:0,behavior:"smooth"})),document.getElementById("close-confirmed-message").addEventListener("click",()=>{o.style.display="none",e.innerHTML=n,e.classList.remove("disabled"),e.disabled=!1})});})();
