(()=>{document.getElementById("complaintForm").addEventListener("submit",async o=>{o.preventDefault();let t=document.querySelector(".confirmed-message"),e=document.getElementById("submitComplaint"),n=e.innerHTML;e.innerHTML="Please, wait...",e.classList.add("disabled"),e.disabled=!0;let s=o.target;(await fetch(s.action,{method:"POST",body:new FormData(s)})).ok&&(s.reset(),setTimeout(()=>{(t.style.display==="none"||t.style.display==="")&&(t.style.display="block",window.scrollTo({top:0,left:0,behavior:"smooth"}))},500)),document.getElementById("close-confirmed-message").addEventListener("click",()=>{t.style.display="none",e.innerHTML=n,e.classList.remove("disabled"),e.disabled=!1})});})();
