(()=>{var e=document.querySelector(".confirmed-message");document.getElementById("complaintForm").addEventListener("submit",function(n){n.preventDefault();let t=document.getElementById("submitComplaint"),o=t.innerHTML;t.innerHTML="Please, wait...",t.classList.add("disabled"),t.disabled=!0,fetch(n.target.action,{method:"POST",body:new FormData(n.target),mode:"no-cors"}).then(()=>{setTimeout(()=>{(e.style.display==="none"||e.style.display==="")&&(e.style.display="block")},500)}).catch(s=>{alert("There was an error of updating Google Form.")}),setTimeout(()=>{e.style.display==="block"&&(e.style.display="none"),t.innerHTML=o,t.classList.remove("disabled")},6e3),document.getElementById("complaintForm").reset()});document.getElementById("close-confirmed-message").addEventListener("click",()=>{e.style.display==="block"&&(e.style.display="none")});})();
