(()=>{document.addEventListener("DOMContentLoaded",function(){o(),c()});function o(){document.querySelectorAll("i.fa-solid.fa-link").forEach(n=>{n.addEventListener("click",e=>{let t=e.target.parentElement.id;if(!t)return;let l=`${window.location.origin}${window.location.pathname}#${t}`;navigator.clipboard.writeText(l),alert("The link is copied.")})})}function c(){document.querySelectorAll(".see-container > h3").forEach(n=>{n.addEventListener("click",()=>{let e=n.nextElementSibling;for(;e;)e.style.display==="block"||e.style.display===""?e.style.display="none":e.style.display="block",e=e.nextElementSibling})})}})();
